import React from "react";
import { BrowserRouter, Route, Routes } from "react-router-dom";
import { ROUTES } from "@common/constants";
import ProtectedRoute from "@components/ProtectedRoute";
import Home from "@pages/home/Home";
import WrapperLogin from "@pages/login/WrapperLogin";
import "@styles/main.scss";
import VerifyEmail from "@components/VerifyEmail";
import { HelmetProvider } from "react-helmet-async";
import Map from "@pages/map/Map";
import SettingUser from "@pages/setting/SettingUser";
import UserManagement from "@pages/users/UserManagement";
import AboutMe from "@pages/about/AboutMe";
import Feedback from "@pages/feedback/Feedback";

const App = () => {
    return (
        <HelmetProvider>
            <BrowserRouter>
                <Routes>
                    <Route path={ROUTES.home} element={<Home />} />
                    <Route path={ROUTES.verify_email} element={<VerifyEmail />} />
                    <Route path={ROUTES.login} element={<WrapperLogin />} />
                    <Route path={ROUTES.feedback} element={<Feedback />} />
                    <Route path={ROUTES.about} element={<AboutMe />} />
                    <Route element={<ProtectedRoute />}>
                        <Route path={ROUTES.map} element={<Map />} />
                        <Route path={ROUTES.users} element={<UserManagement />} />
                        <Route path={ROUTES.setting} element={<SettingUser />} />
                    </Route>
                </Routes>
            </BrowserRouter>
        </HelmetProvider>
    );
};

export default App;

import React from "react";
import { NavLink } from "react-router-dom";
import { motion } from "framer-motion";

const Banner = ({ backgroundImage, title, description, buttonText, buttonLink }) => {
    return (
        <motion.div
            className="banner"
            style={{ backgroundImage: `url(${backgroundImage})` }}
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1 }}
        >
            <div className="content">
                <h1>{title}</h1>
                <div className="divider"></div>
                <p>{description}</p>
                {buttonLink && (
                    <NavLink to={buttonLink}>
                        <button className="btn-primary">{buttonText}</button>
                    </NavLink>
                )}
            </div>
        </motion.div>
    );
};

export default Banner;

L.TileLayer.BetterWMS = L.TileLayer.WMS.extend({
    // Khi th√™m l·ªõp v√†o b·∫£n ƒë·ªì
    onAdd: function (map) {
        L.TileLayer.WMS.prototype.onAdd.call(this, map);
        map.on("click", this.getFeatureInfo, this);
    },

    // Khi b·ªè l·ªõp kh·ªèi b·∫£n ƒë·ªì
    onRemove: function (map) {
        L.TileLayer.WMS.prototype.onRemove.call(this, map);
        map.off("click", this.getFeatureInfo, this);
    },

    // G·ª≠i y√™u c·∫ßu GetFeatureInfo
    getFeatureInfo: function (evt) {
        var url = this.getFeatureInfoUrl(evt.latlng);
        var showResults = L.Util.bind(this.showGetFeatureInfo, this);
        fetch(url)
            .then((res) => res.text())
            .then((data) => {
                showResults(null, evt.latlng, data);
            })
            .catch((error) => {
                showResults(error);
            });
    },

    // T·∫°o URL GetFeatureInfo
    getFeatureInfoUrl: function (latlng) {
        var point = this._map.latLngToContainerPoint(latlng, this._map.getZoom());
        var size = this._map.getSize();
        var params = {
            request: "GetFeatureInfo",
            service: "WMS",
            srs: "EPSG:4326",
            styles: this.wmsParams.styles || "", // ƒê·∫£m b·∫£o c√≥ gi√° tr·ªã h·ª£p l·ªá
            transparent: this.wmsParams.transparent || true, // ƒê·∫£m b·∫£o c√≥ gi√° tr·ªã h·ª£p l·ªá
            version: this.wmsParams.version || "1.1.1", // ƒê·∫£m b·∫£o c√≥ gi√° tr·ªã h·ª£p l·ªá
            format: "image/png", // ƒê·ªãnh d·∫°ng h√¨nh ·∫£nh
            bbox: this._map.getBounds().toBBoxString(),
            height: size.y,
            width: size.x,
            layers: this.wmsParams.layers,
            query_layers: this.wmsParams.layers,
            info_format: "application/json", // ƒê·ªãnh d·∫°ng tr·∫£ v·ªÅ JSON
        };

        // Ch·ªçn ƒë√∫ng tham s·ªë `x` v√† `y` ho·∫∑c `i` v√† `j` d·ª±a tr√™n phi√™n b·∫£n
        if (params.version === "1.3.0") {
            params.i = point.x;
            params.j = point.y;
        } else {
            params.x = point.x;
            params.y = point.y;
        }

        // Tr·∫£ v·ªÅ URL v·ªõi c√°c tham s·ªë GET
        return this._url + L.Util.getParamString(params, this._url, true);
    },

    // Hi·ªÉn th·ªã k·∫øt qu·∫£ GetFeatureInfo
    showGetFeatureInfo: function (err, latlng, content) {
        if (err) {
            console.log("Error:", err);
            return;
        }

        try {
            const featureInfo = JSON.parse(content);
            const feature = featureInfo.features[0];
            const props = feature?.properties;

            if (!props) return;

            const layerName = this.wmsParams.layers.split(":")[1];

            const legendNames = {
                salinityPoints: "ƒêi·ªÉm ƒëo m·∫∑n",
                hydrometStations: "Tr·∫°m kh√≠ t∆∞·ª£ng th·ªßy vƒÉn",
                DiaPhanHuyen: "ƒê·ªãa ph·∫≠n huy·ªán",
                DiaGioiHuyen: "ƒê·ªãa gi·ªõi huy·ªán",
                DiaPhanXa: "ƒê·ªãa ph·∫≠n x√£",
                DiemDoCao: "ƒêi·ªÉm ƒë·ªô cao",
                ThuyHe_line: "Th·ªßy h·ªá (ƒë∆∞·ªùng)",
                ThuyHe_polygon: "Th·ªßy h·ªá (v√πng)",
                GiaoThong_line: "Giao th√¥ng (ƒë∆∞·ªùng)",
                GiaoThong_polygon: "Giao th√¥ng (v√πng)",
                HienTrangSDD_2020: "Hi·ªán tr·∫°ng s·ª≠ d·ª•ng ƒë·∫•t 2020",
                QuyHoachSDD_2030: "Quy ho·∫°ch s·ª≠ d·ª•ng ƒë·∫•t 2030",
            };
            let popupContent = `
      <div style="font-family: 'Segoe UI', sans-serif; font-size: 14px; line-height: 1.6; padding: 6px 10px;">
        <div style="font-weight: bold; color: #2c3e50; font-size: 16px; margin-bottom: 5px; border-bottom: 1px solid #ccc;">
          üóÇÔ∏è Th√¥ng tin l·ªõp: ${legendNames[layerName]}
        </div>
    `;

            switch (layerName) {
                case "DiaPhanHuyen":
                    popupContent += `
          <b>üìç Huy·ªán:</b> ${props.tenhuyen}<br/>
          <b>üÜî M√£ huy·ªán:</b> ${props.mahuyen}<br/>
          <b>üìê Di·ªán t√≠ch:</b> ${props.dientichtunhien} m¬≤
        `;
                    break;
                case "DiaPhanXa":
                    popupContent += `
          <b>üìç X√£:</b> ${props.tenxa}<br/>
          <b>üÜî M√£ x√£:</b> ${props.maxa}<br/>
          <b>üèûÔ∏è Huy·ªán:</b> ${props.tenhuyen}<br/>
          <b>üìê Di·ªán t√≠ch:</b> ${props.dientichtunhien} m¬≤
        `;
                    break;
                case "DiemDoCao":
                    popupContent += `<b>üìè ƒê·ªô cao:</b> ${props.docao_m} m`;
                    break;
                case "GiaoThong_line":
                    popupContent += `
          <b>üöß T√™n ƒë∆∞·ªùng:</b> ${props.tenduong}<br/>
          <b>üìè Chi·ªÅu d√†i:</b> ${props.chieudai} m
        `;
                    break;
                case "GiaoThong_polygon":
                    popupContent += `
          <b>üöß T√™n ƒë∆∞·ªùng:</b> ${props.tenduong}<br/>
          <b>‚ÜîÔ∏è R·ªông:</b> ${props.dorong} m<br/>
          <b>‚ÜïÔ∏è D√†i:</b> ${props.chieudai} m<br/>
          <b>üß± K·∫øt c·∫•u:</b> ${props.ketcau}<br/>
          <b>üõ†Ô∏è T√¨nh tr·∫°ng:</b> ${props.tinhtrang}
        `;
                    break;
                case "ThuyHe_line":
                    popupContent += `
          <b>üåä T√™n s√¥ng/k√™nh:</b> ${props.ten}<br/>
          <b>üîº ƒêi·ªÉm ƒë·∫ßu:</b> ${props.diemdau}<br/>
          <b>üîΩ ƒêi·ªÉm cu·ªëi:</b> ${props.diemcuoi}<br/>
          <b>üìè D√†i:</b> ${props.chieudai} m
        `;
                    break;
                case "ThuyHe_polygon":
                    popupContent += `
          <b>üåä T√™n:</b> ${props.ten}<br/>
          <b>üìñ Lo·∫°i:</b> ${props.phanloai}<br/>
          <b>‚ÜîÔ∏è R·ªông:</b> ${props.dorong} m<br/>
          <b>‚ÜïÔ∏è S√¢u:</b> ${props.dosau} m<br/>
          <b>‚ö†Ô∏è Tr·∫°ng th√°i:</b> ${props.trangthai}
        `;
                    break;
                case "HienTrangSDD_2020":
                    popupContent += `
          <b>üå± Lo·∫°i ƒë·∫•t:</b> ${props.loaidat}<br/>
          <b>üî¢ K√Ω hi·ªáu:</b> ${props.kihieu}
        `;
                    break;
                case "QuyHoachSDD_2030":
                    popupContent += `
          <b>üèóÔ∏è Lo·∫°i ƒë·∫•t:</b> ${props.loaidat}<br/>
          <b>üìö Ph√¢n lo·∫°i:</b> ${props.phanloai}<br/>
          <b>üî¢ K√Ω hi·ªáu:</b> ${props.kihieu}
        `;
                    break;
                default:
                    popupContent += `<i>Kh√¥ng c√≥ c·∫•u h√¨nh hi·ªÉn th·ªã cho l·ªõp n√†y.</i>`;
            }

            popupContent += `</div>`; // k·∫øt th√∫c container

            L.popup().setLatLng(latlng).setContent(popupContent).openOn(this._map);
        } catch (e) {
            console.log("L·ªói khi x·ª≠ l√Ω d·ªØ li·ªáu JSON:", e);
        }
    },
});

// H√†m t·∫°o l·ªõp BetterWMS
L.tileLayer.betterWms = function (url, options) {
    return new L.TileLayer.BetterWMS(url, options);
};

import React, { useEffect, useState, useMemo } from "react";
import { NavLink } from "react-router-dom";
import { ROUTES } from "@common/constants";
import imageLogo from "@assets/logo.png";
import { menusGIS } from "@pages/map/dataLayers";

function LeftMenuMap({
    sidebarOpen,
    setSidebarOpen,
    onLayerToggle,
    searchResults,
    setSelectedLocation,
    setHighlightedFeature,
    highlightedFeature,
}) {
    const [state, setState] = useState({
        openMenuIndex: null,
        enabledLayers: [],
        activeTab: "Data", // M·∫∑c ƒë·ªãnh l√† tab Data
        isLoadingSearchResults: true,
    });
    const [districtList, setDistrictList] = useState([]);
    const [selectedDistrict, setSelectedDistrict] = useState(null);
    const [selectedItemId, setSelectedItemId] = useState(null);

    const handleSalinityPointsToggle = (checked) => {
        setState((prevState) => ({
            ...prevState,
            enabledLayers: checked
                ? [...prevState.enabledLayers, "salinityPoints"]
                : prevState.enabledLayers.filter((layer) => layer !== "salinityPoints"),
        }));

        onLayerToggle("salinityPoints", checked);
    };

    const handleHydrometStationsToggle = (checked) => {
        setState((prevState) => ({
            ...prevState,
            enabledLayers: checked
                ? [...prevState.enabledLayers, "hydrometStations"]
                : prevState.enabledLayers.filter((layer) => layer !== "hydrometStations"),
        }));

        onLayerToggle("hydrometStations", checked);
    };

    useEffect(() => {
        if (searchResults && searchResults.length > 0) {
            setState((prevState) => ({
                ...prevState,
                activeTab: "search",
                isLoadingSearchResults: false,
            }));
        } else {
            setState((prevState) => ({
                ...prevState,
                isLoadingSearchResults: false,
            }));
        }
    }, [searchResults]);

    const toggleDropdown = (index) => {
        setState((prevState) => ({
            ...prevState,
            openMenuIndex: prevState.openMenuIndex === index ? null : index,
        }));
    };

    const handleLayerToggle = async (layer, checked) => {
        const updatedLayers = checked
            ? [...state.enabledLayers, layer]
            : state.enabledLayers.filter((l) => l !== layer);

        onLayerToggle(layer, checked);

        if (layer === "DiaPhanHuyen" && checked) {
            try {
                const res = await fetch(`${import.meta.env.VITE_BASE_URL}/api/districts`);
                const data = await res.json();
                console.log("Districts data:", data);

                setDistrictList(data);
            } catch (error) {
                console.error("L·ªói khi l·∫•y danh s√°ch huy·ªán:", error);
            }
        }

        setState((prevState) => ({
            ...prevState,
            enabledLayers: updatedLayers,
        }));
    };

    const getBoundsFromCoordinates = (coordinates) => {
        let lats = [];
        let lngs = [];

        const extractCoords = (coords) => {
            coords.forEach((c) => {
                if (typeof c[0] === "number" && typeof c[1] === "number") {
                    lngs.push(c[0]);
                    lats.push(c[1]);
                } else {
                    extractCoords(c);
                }
            });
        };

        extractCoords(coordinates);

        const southWest = [Math.min(...lats), Math.min(...lngs)];
        const northEast = [Math.max(...lats), Math.max(...lngs)];

        return [southWest, northEast];
    };

    const handleClick = (result) => {
        try {
            if (result.type === "diem_do_man") {
                const lat = result.ViDo;
                const lng = result.KinhDo;
                if (lat && lng) {
                    setSelectedLocation({ lat, lng, zoom: 15 });
                    setHighlightedFeature({
                        type: "Point",
                        coordinates: [lng, lat],
                        icon: "droplet",
                        name: result.TenDiem,
                    });
                }
                return;
            }

            const geojson = result.geom;
            if (!geojson || !geojson.type) return;

            if (geojson.type === "Point") {
                const [lng, lat] = geojson.coordinates;
                setSelectedLocation({ lat, lng, zoom: 14 });
                setHighlightedFeature(geojson);
            } else if (geojson.type === "Polygon" || geojson.type === "MultiPolygon") {
                const bounds = getBoundsFromCoordinates(geojson.coordinates);
                setSelectedLocation({ bounds });
                setHighlightedFeature(geojson);
            }
        } catch (err) {
            console.error("L·ªói x·ª≠ l√Ω GeoJSON ho·∫∑c t·ªça ƒë·ªô:", err);
        }
    };

    const renderTabContent = useMemo(() => {
        if (state.activeTab === "Data") {
            return (
                <div className="tab-content-data">
                    {/* Monitoring Data Section */}
                    <div className="data-section">
                        <div className="section-header">
                            <i className="fa-solid fa-chart-line section-icon"></i>
                            <h3 className="section-title">D·ªØ li·ªáu quan tr·∫Øc</h3>
                        </div>
                        <div className="monitoring-layers">
                            <div className="layer-item monitoring-layer">
                                <div className="layer-toggle">
                                    <input
                                        type="checkbox"
                                        id="layer-salinity-points"
                                        className="layer-checkbox"
                                        checked={state.enabledLayers.includes("salinityPoints")}
                                        onChange={(e) => handleSalinityPointsToggle(e.target.checked)}
                                    />
                                    <label htmlFor="layer-salinity-points" className="layer-label">
                                        <div className="layer-info">
                                            <div className="layer-icon-wrapper salinity-icon">
                                                <i className="fa-solid fa-droplet"></i>
                                            </div>
                                            <div className="layer-details">
                                                <span className="layer-name">ƒêi·ªÉm ƒëo m·∫∑n</span>
                                                <span className="layer-desc">C√°c ƒëi·ªÉm quan tr·∫Øc ƒë·ªô m·∫∑n</span>
                                            </div>
                                        </div>
                                    </label>
                                </div>
                            </div>

                            <div className="layer-item monitoring-layer">
                                <div className="layer-toggle">
                                    <input
                                        type="checkbox"
                                        id="layer-hydromet-stations"
                                        className="layer-checkbox"
                                        checked={state.enabledLayers.includes("hydrometStations")}
                                        onChange={(e) => handleHydrometStationsToggle(e.target.checked)}
                                    />
                                    <label htmlFor="layer-hydromet-stations" className="layer-label">
                                        <div className="layer-info">
                                            <div className="layer-icon-wrapper hydromet-icon">
                                                <i className="fa-solid fa-tower-observation"></i>
                                            </div>
                                            <div className="layer-details">
                                                <span className="layer-name">Tr·∫°m kh√≠ t∆∞·ª£ng th·ªßy vƒÉn</span>
                                                <span className="layer-desc">Tr·∫°m quan tr·∫Øc th·ªùi ti·∫øt</span>
                                            </div>
                                        </div>
                                    </label>
                                </div>
                            </div>
                        </div>
                    </div>

                    {/* GIS Data Sections */}
                    {menusGIS.map((group, groupIndex) => (
                        <div className="data-section" key={groupIndex}>
                            <div className="section-header">
                                <i className={`${group.icon || "fa-solid fa-layer-group"} section-icon`}></i>
                                <h3 className="section-title">{group.title}</h3>
                            </div>
                            <div className="gis-categories">
                                {group.items.map((menu, itemIndex) => {
                                    const uniqueIndex = `${groupIndex}-${itemIndex}`;
                                    const isOpen = state.openMenuIndex === uniqueIndex;

                                    return (
                                        <div className="category-item" key={uniqueIndex}>
                                            <div
                                                className={`category-header ${isOpen ? "active" : ""}`}
                                                onClick={() => toggleDropdown(uniqueIndex)}
                                            >
                                                <div className="category-info">
                                                    <i className={`${menu.icon} category-icon`}></i>
                                                    <span className="category-name">{menu.name}</span>
                                                </div>
                                                <i
                                                    className={`fa-solid fa-chevron-right expand-icon ${
                                                        isOpen ? "rotated" : ""
                                                    }`}
                                                ></i>
                                            </div>

                                            {isOpen && (
                                                <div className="category-layers">
                                                    {menu.layers.map((layer, idx) => (
                                                        <div
                                                            className="layer-item"
                                                            key={`${uniqueIndex}-${idx}`}
                                                        >
                                                            <div className="layer-toggle">
                                                                <input
                                                                    type="checkbox"
                                                                    id={`layer-${layer}`}
                                                                    className="layer-checkbox"
                                                                    checked={state.enabledLayers.includes(
                                                                        layer,
                                                                    )}
                                                                    onChange={(e) =>
                                                                        handleLayerToggle(
                                                                            layer,
                                                                            e.target.checked,
                                                                        )
                                                                    }
                                                                />
                                                                <label
                                                                    htmlFor={`layer-${layer}`}
                                                                    className="layer-label"
                                                                >
                                                                    <span className="layer-name">
                                                                        {menu.nameItem?.[idx] || layer}
                                                                    </span>
                                                                </label>
                                                            </div>
                                                        </div>
                                                    ))}
                                                </div>
                                            )}
                                        </div>
                                    );
                                })}
                            </div>
                        </div>
                    ))}

                    {/* District Selection */}
                    {state.enabledLayers.includes("DiaPhanHuyen") && districtList.length > 0 && (
                        <div className="data-section">
                            <div className="section-header">
                                <i className="fa-solid fa-map-location-dot section-icon"></i>
                                <h3 className="section-title">L·ªçc theo huy·ªán</h3>
                            </div>
                            <div className="district-selector">
                                <select
                                    className="district-select"
                                    value={selectedDistrict || ""}
                                    onChange={(e) => {
                                        const district = districtList.find((d) => d.name === e.target.value);
                                        setSelectedDistrict(e.target.value);
                                        if (district) {
                                            setSelectedLocation({
                                                lat: district.centerLat,
                                                lng: district.centerLng,
                                                zoom: 12,
                                            });
                                        }
                                    }}
                                >
                                    <option value="">-- Ch·ªçn huy·ªán --</option>
                                    {districtList.map((d, idx) => (
                                        <option key={idx} value={d.name}>
                                            {d.name}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                    )}
                </div>
            );
        }

        if (state.activeTab === "search") {
            return (
                <div className="tab-content-search">
                    {state.isLoadingSearchResults ? (
                        <div className="search-state">
                            <div className="loading-spinner">
                                <i className="fa-solid fa-spinner fa-spin"></i>
                            </div>
                            <p className="state-message">ƒêang t·∫£i d·ªØ li·ªáu...</p>
                        </div>
                    ) : Array.isArray(searchResults) && searchResults.length === 0 ? (
                        <div className="search-state">
                            <div className="empty-icon">
                                <i className="fa-solid fa-magnifying-glass"></i>
                            </div>
                            <p className="state-message">Kh√¥ng t√¨m th·∫•y k·∫øt qu·∫£.</p>
                        </div>
                    ) : Array.isArray(searchResults) ? (
                        <div className="search-results">
                            {searchResults.map((result, idx) => (
                                <div
                                    key={result.id || idx}
                                    className={`result-card ${
                                        selectedItemId === result.id ? "selected" : ""
                                    }`}
                                    onClick={() => handleClick(result)}
                                >
                                    {result.type === "diem_do_man" ? (
                                        <div className="result-content salinity-result">
                                            <div className="result-header">
                                                <div className="result-icon">
                                                    <i className="fa-solid fa-droplet"></i>
                                                </div>
                                                <div className="result-title">
                                                    <h4 className="result-name">{result.TenDiem}</h4>
                                                    <span className="result-type">ƒêi·ªÉm ƒëo m·∫∑n</span>
                                                </div>
                                            </div>
                                            <div className="result-details">
                                                <div className="detail-item">
                                                    <span className="detail-label">Ph√¢n lo·∫°i:</span>
                                                    <span className="detail-value">{result.PhanLoai}</span>
                                                </div>
                                                <div className="detail-item">
                                                    <span className="detail-label">T·ªça ƒë·ªô:</span>
                                                    <span className="detail-value">
                                                        {result.ViDo}, {result.KinhDo}
                                                    </span>
                                                </div>
                                                <div className="detail-item">
                                                    <span className="detail-label">Th·ªùi gian:</span>
                                                    <span className="detail-value">{result.ThoiGian}</span>
                                                </div>
                                                <div className="detail-item">
                                                    <span className="detail-label">T·∫ßn su·∫•t:</span>
                                                    <span className="detail-value">{result.TanSuat}</span>
