        return newErrors;
    };

    const checkEmailExists = async (email) => {
        try {
            const response = await axiosInstance.get(
                import.meta.env.VITE_BASE_URL + `/api/feedback/${email}`,
            );
            if (response.data) {
                setFormData(response.data);
                setIsUpdate(true);
