import { useRef, useState } from "react";
import { useDispatch } from "react-redux";
import { signUp } from "@stores/actions/authActions";
import { ToastCommon } from "@components/ToastCommon";
import { TOAST } from "@common/constants";

// eslint-disable-next-line react/prop-types
function SignUp() {
    const dispatch = useDispatch();
    const [errorMessage, setErrorMessage] = useState("");
    const email = useRef(null);
    const password = useRef(null);
    const confirmPassword = useRef(null);
    const name = useRef(null);

    const handleKeyPress = (event, nextFieldRef) => {
        if (event.key === "Enter") {
            event.preventDefault(); // NgƒÉn ch·∫∑n h√†nh vi submit m·∫∑c ƒë·ªãnh
            if (nextFieldRef) {
                nextFieldRef.current.focus(); // Di chuy·ªÉn ti√™u ƒëi·ªÉm ƒë·∫øn tr∆∞·ªùng ti·∫øp theo
            } else {
                handleSignUp(); // G·ªçi h√†m ƒëƒÉng k√Ω n·∫øu ƒë√£ ƒë·∫øn √¥ cu·ªëi
            }
        }
    };

    const handleSignUp = async () => {
        try {
            // G·ªçi action ƒëƒÉng k√Ω
            await dispatch(
                signUp({
                    name: name.current.value,
                    email: email.current.value,
                    password: password.current.value,
                    confirmPassword: confirmPassword.current.value,
                }),
            );

            // Sau khi ƒëƒÉng k√Ω th√†nh c√¥ng, g·ª≠i email x√°c minh
            await dispatch(sendVerificationEmail(email.current.value));

            ToastCommon(
                TOAST.SUCCESS,
                "Successfully registered! Please check your email to verify your account.",
            );
        } catch (error) {
            setErrorMessage(error?.response?.data?.message || "An error occurred during registration.");
        }
    };

    return (
        <div className="signUp">
            <form onSubmit={(e) => e.preventDefault()}>
                <label className="labelLogin" htmlFor="chk" aria-hidden="true">
                    ƒêƒÉng k√Ω
                </label>
                <input
                    className="inputLogin"
                    type="text"
                    name="name"
                    placeholder="Name"
                    ref={name}
                    onKeyDown={(e) => handleKeyPress(e, email)}
                />
                <input
                    className="inputLogin"
                    type="email"
                    name="email"
                    placeholder="Email"
                    ref={email}
                    onKeyDown={(e) => handleKeyPress(e, password)}
                />
                <input
                    className="inputLogin"
                    type="password"
                    name="pswd"
                    placeholder="Password"
                    ref={password}
                    onKeyDown={(e) => handleKeyPress(e, confirmPassword)}
                />
                <input
                    className="inputLogin"
                    type="password"
                    name="pswd"
                    placeholder="Confirm Password"
                    ref={confirmPassword}
                    onKeyDown={(e) => handleKeyPress(e, null)}
                />
                <button type="button" className="btnLogin btnLoginSubmit" onClick={() => handleSignUp()}>
                    Sign up
                </button>
            </form>
        </div>
    );
}

export default SignUp;

