    // Handle location changes
    useEffect(() => {
        handleLocationChange(map, selectedLocation);
    }, [selectedLocation, map]);

    // Handle feature highlighting
    useEffect(() => {
        handleFeatureHighlight(map, highlightedFeature, highlightedLayerRef, highlightedMarkerRef);
