            if (value < 1) color = "blue";
            else if (value < 4) color = "#fd7e14";
            else color = "#dc3545";

            if (lat == null || lng == null || isNaN(lat) || isNaN(lng)) {
                console.warn(`‚ö†Ô∏è Kh√¥ng th·ªÉ chuy·ªÉn t·ªça ƒë·ªô t·∫°i ƒëi·ªÉm ${point.TenDiem}`);
