                            break;
                        case "(¬∞C)": // Temperature
                            color = "#dc3545"; // Red for temperature
                            break;
                        case "(cm)": // Water level
                            color = "#198754"; // Green for water level
                            break;
                        default:
                            color = "#6c757d"; // Gray for unknown types
                    }

                    return `
          <div class="popup-main-value">
            <span class="value-label">${contentLabel || "Gi√° tr·ªã ƒëo"}</span>
            <span class="value-number" style="color: ${color}">
              ${!isNaN(value) ? value.toFixed(2) : "-"} ${DonVi}
            </span>
          </div>
        `;
                })
                .join("");

            const popupHTML = `
        <div class="modern-popup">
          <div class="popup-header">
            <div class="popup-icon">üå§Ô∏è</div>
            <div class="popup-title">
              <h4 class="popup-name">${point.TenTam}</h4>
              <span class="popup-type">${point.PhanLoai || "Tr·∫°m kh√≠ t∆∞·ª£ng th·ªßy vƒÉn"}</span>
            </div>
          </div>

          <div class="popup-content">
            ${valueRows}
            <div class="popup-details">
              <div class="detail-grid">
                <div class="detail-item py-2">
                  <div class="detail-content">
                    <strong class="detail-label"><i class="detail-icon">üè≠</i> M√£ tr·∫°m:</strong>
                    <span class="detail-value">${point.KiHieu}</span>
                  </div>
                </div>

                <div class="detail-item py-2">
                  <div class="detail-content">
                    <strong class="detail-label"><i class="detail-icon">üìÖ</i> Ng√†y ƒëo:</strong>
                    <span class="detail-value">${date}</span>
                  </div>
                </div>

                <div class="detail-item py-2">
                  <div class="detail-content">
                    <strong class="detail-label"> <i class="detail-icon">üìä</i> Y·∫øu t·ªë:</strong>
                    <span class="detail-value">${point.YeuTo || "Kh√¥ng x√°c ƒë·ªãnh"}</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      `;

            marker.bindPopup(popupHTML);
        });

        if (latLngs.length > 0) {
            const bounds = L.latLngBounds(latLngs);
            mapInstance.fitBounds(bounds, {
                padding: [50, 50],
                animate: true,
            });
        }
    };

    return (
        <>
            <div ref={mapContainer} id="mapContainer"></div>

            <MapDetails
                salinityData={salinityData}
                selectedPoint={selectedPoint}
                hydrometData={hydrometData}
                onOpenFullChart={() => setShowFullChart(true)}
                onClose={handleCloseDetails}
            />

            <SaltChartFull
                show={showFullChart}
                kiHieu={selectedPoint?.kiHieu}
                tenDiem={selectedPoint?.tenDiem}
                salinityData={salinityData}
                hydrometData={hydrometData}
                onClose={() => setShowFullChart(false)}
            />
        </>
    );
};

export default MapboxMap;

import React from "react";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, Cell } from "recharts";

const calculateTrend = (current, previous) => {
    if (!previous) return null;
    const diff = current - previous;
    return {
        difference: diff.toFixed(2),
        direction: diff > 0 ? "increase" : diff < 0 ? "decrease" : "stable",
        percentage: ((Math.abs(diff) / previous) * 100).toFixed(1),
    };
};

const CustomTooltip = ({ active, payload }) => {
    if (active && payload?.length) {
        const currentValue = payload[0].payload;
        const formattedDate = new Date(currentValue.date).toLocaleDateString("vi-VN");
        const trend = currentValue.trend;

        return (
            <div className="bg-white border p-2 rounded shadow-sm">
                <p className="mb-1">Ng√†y: {formattedDate}</p>
                <p className="mb-1">ƒê·ªô m·∫∑n: {Number(currentValue.salinity).toFixed(2)} ‚Ä∞</p>
                {trend && (
                    <p
                        className="mb-0"
                        style={{
                            color:
                                trend.direction === "increase"
                                    ? "#dc3545"
                                    : trend.direction === "decrease"
                                      ? "#198754"
                                      : "#6c757d",
                        }}
                    >
                        {trend.direction === "increase" ? "‚ñ≤" : trend.direction === "decrease" ? "‚ñº" : "‚ñ†"}
                        {trend.direction === "increase"
                            ? "TƒÉng"
                            : trend.direction === "decrease"
                              ? "Gi·∫£m"
                              : "Kh√¥ng ƒë·ªïi"}{" "}
                        {Math.abs(trend.difference)} ‚Ä∞ ({trend.percentage}%)
                    </p>
                )}
            </div>
        );
    }
    return null;
};

const SalinityBarChart = ({ data, height }) => {
    // Ensure salinity values are numbers and calculate trends
    const formattedData = data.map((item, index) => {
        const salinity = Number(item.salinity);
        const previousItem = index > 0 ? data[index - 1] : null;
        const previousSalinity = previousItem ? Number(previousItem.salinity) : null;

        return {
            ...item,
            salinity,
            trend: calculateTrend(salinity, previousSalinity),
        };
    });

    const isLoading = !formattedData || formattedData.length === 0;

    const uniqueYears = [...new Set(formattedData.map((d) => new Date(d.date).getFullYear()))];

    const ticksByYear = uniqueYears.map(
        (year) => formattedData.find((d) => new Date(d.date).getFullYear() === year)?.date,
    );

    return (
        <>
            {isLoading ? (
                <div className="text-center py-5">
                    <div className="spinner-border text-primary" role="status">
                        <span className="visually-hidden">Loading...</span>
                    </div>
                    <p className="mt-2">ƒêang t·∫£i d·ªØ li·ªáu...</p>
                </div>
            ) : (
                <ResponsiveContainer width="100%" height="100%" minHeight={height}>
                    <BarChart data={formattedData} barCategoryGap={8}>
                        <CartesianGrid strokeDasharray="3 3" stroke="#dee2e6" />

                        <XAxis
                            dataKey="date"
                            tickFormatter={(d) => new Date(d).getFullYear()}
                            ticks={ticksByYear}
                            tick={{ fontSize: 10 }}
                        />

                        <YAxis
                            unit="‚Ä∞"
                            tick={{
                                fontSize: 12,
                            }}
                            domain={[0, (dataMax) => Math.ceil(dataMax)]}
                            allowDataOverflow={true}
                        />
                        <Tooltip content={<CustomTooltip />} />
                        <Bar dataKey="salinity" barSize={10}>
                            {formattedData.map((entry, index) => {
                                let fillColor = "#0d6efd";
                                if (entry.salinity >= 4) fillColor = "#dc3545";
                                else if (entry.salinity >= 1) fillColor = "#fd7e14";
                                return <Cell key={`cell-${index}`} fill={fillColor} />;
                            })}
                        </Bar>
                    </BarChart>
                </ResponsiveContainer>
            )}
            <div className="d-flex justify-content-center gap-3 mt-2 small">
                <div className="d-flex align-items-center gap-1">
                    <div
                        style={{
                            width: 12,
                            height: 12,
                            backgroundColor: "#0d6efd",
                            borderRadius: 2,
                        }}
                    ></div>
                    <span>B√¨nh th∆∞·ªùng (ƒë·ªô m·∫∑n &lt; 1‚Ä∞)</span>
                </div>
                <div className="d-flex align-items-center gap-1">
                    <div
                        style={{
                            width: 12,
                            height: 12,
                            backgroundColor: "#fd7e14",
                            borderRadius: 2,
                        }}
                    ></div>
                    <span>R·ªßi ro c·∫•p 2 (ƒë·ªô m·∫∑n 1‚Ä∞ - 4‚Ä∞)</span>
                </div>
                <div className="d-flex align-items-center gap-1">
                    <div
                        style={{
