const SaltChartFull = ({ show, kiHieu, tenDiem, salinityData, onClose }) => {
    const [data, setData] = useState([]);

    useEffect(() => {
        if (show && salinityData) {
            setData(salinityData);
        } else {
            setData([]);
        }
    }, [show, salinityData]);

    useEffect(() => {
        if (!show) setData([]);
    }, [show]);

    const handleExportExcel = async () => {
        try {
            const res = await axiosInstance.get(`/api/salinity-export/${kiHieu}`, {
                responseType: "blob",
            });

            const blob = new Blob([res.data], {
                type: "application/vnd.openxmlformats-officedocument.spreadsheetml.sheet",
            });

            const url = window.URL.createObjectURL(blob);
            const link = document.createElement("a");
            link.href = url;
            link.setAttribute("download", `DoMan_${tenDiem || kiHieu}.xlsx`);
            document.body.appendChild(link);
            link.click();
            link.remove();
        } catch (error) {
            console.error("‚ùå Xu·∫•t Excel l·ªói:", error);
            alert("Kh√¥ng th·ªÉ xu·∫•t d·ªØ li·ªáu t·ª´ m√°y ch·ªß.");
        }
    };

    const validData = data?.filter(
        (item) => item.salinity !== null && item.salinity !== "NULL" && !isNaN(item.salinity),
    );

    const startDate = validData?.length > 0 ? new Date(validData[0].date).toLocaleDateString("vi-VN") : null;
    const endDate =
        validData?.length > 0
            ? new Date(validData[validData.length - 1].date).toLocaleDateString("vi-VN")
            : null;

    return (
        <div
            className={`modal fade ${show ? "show d-block" : ""}`}
            tabIndex="-1"
            style={{ backgroundColor: show ? "rgba(0,0,0,0.5)" : "transparent" }}
        >
            <div className="modal-dialog modal-lg modal-dialog-centered">
                <div className="modal-content">
                    <div className="modal-header border-0 pb-0">
                        <div className="w-100 text-center">
                            <h5 className="modal-title mb-1 fw-bold">Bi·ªÉu ƒë·ªì x√¢m nh·∫≠p m·∫∑n - {tenDiem}</h5>
                            {startDate && endDate && (
                                <div className="text-muted small">
                                    T·ª´ <strong>{startDate}</strong> ƒë·∫øn <strong>{endDate}</strong>
                                </div>
                            )}
                        </div>
                        <button
                            type="button"
                            className="btn-close position-absolute end-0 top-0 m-3"
                            onClick={onClose}
                        ></button>
                    </div>

                    <div className="modal-body">
                        <ul className="nav nav-tabs mb-3" role="tablist">
                            <li className="nav-item">
                                <button
                                    className="nav-link active"
                                    id="chart-tab"
                                    data-bs-toggle="tab"
                                    data-bs-target="#chart"
                                    type="button"
                                >
                                    Bi·ªÉu ƒë·ªì
                                </button>
                            </li>
                            <li className="nav-item">
                                <button
                                    className="nav-link"
                                    id="export-tab"
                                    data-bs-toggle="tab"
                                    data-bs-target="#export"
                                    type="button"
                                >
                                    Xu·∫•t d·ªØ li·ªáu
                                </button>
                            </li>
                        </ul>

                        <div className="tab-content">
                            <div className="tab-pane fade show active" id="chart">
                                {data.length > 0 ? (
                                    <SalinityBarChart data={data} height={350} />
                                ) : (
                                    <p className="text-muted">Kh√¥ng c√≥ d·ªØ li·ªáu.</p>
                                )}
                            </div>

                            <div className="tab-pane fade" id="export">
