import Header from "@pages/themes/headers/Header";

function SettingUser() {
    const dispatch = useDispatch();
    const { userInfo } = useSelector((state) => state.authStore);

    const formatDateForDB = (dateString) => {
        if (!dateString) return null; // Tr√°nh g·ª≠i undefined
        const parts = dateString.split("/");
        if (parts.length === 3) {
            return `${parts[2]}-${parts[1]}-${parts[0]}`; // Chuy·ªÉn t·ª´ DD/MM/YYYY ‚Üí YYYY-MM-DD
        }
        return dateString; // N·∫øu ƒë√£ ƒë√∫ng format th√¨ gi·ªØ nguy√™n
    };

    const [formState, setFormState] = useState({
        name: userInfo?.name || "",
        email: userInfo?.email || "",
        password: "",
        confirmPassword: "",
        birthday: userInfo?.birthday || "",
        phone: userInfo?.phone || "",
    });

    const [toggleUpdatePassword, setToggleUpdatePassword] = useState(false);
    const [showConfirmModal, setShowConfirmModal] = useState(false);
    const [errorMessages, setErrorMessages] = useState({});
    const [updatedFormState, setUpdatedFormState] = useState({});

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormState((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = () => {
        const errors = {};

        if (toggleUpdatePassword) {
            if (!formState.password) {
                errors.password = REQUIRE_PASSWORD;
            } else if (formState.password !== formState.confirmPassword) {
                errors.confirmPassword = "M·∫≠t kh·∫©u kh√¥ng kh·ªõp";
            }
        }

        // Ki·ªÉm tra s·ªë ƒëi·ªán tho·∫°i h·ª£p l·ªá
        if (formState.phone && !/^\d{10,11}$/.test(formState.phone)) {
            errors.phone = "S·ªë ƒëi·ªán tho·∫°i kh√¥ng h·ª£p l·ªá";
        }

        if (Object.keys(errors).length > 0) {
            setErrorMessages(errors);
            return;
        }

        const updatedData = {
            ...formState,
            birthday: formatDateForDB(formState.birthday), // Chuy·ªÉn ƒë·ªïi ng√†y sinh
        };

        setUpdatedFormState(updatedData);
        setShowConfirmModal(true);
    };

    const confirmAction = () => {
        dispatch(updateUserByUser(updatedFormState));
        setShowConfirmModal(false);
    };

    return (
        <>
            <Helmet>
                <title>C√†i ƒë·∫∑t | X√¢m nh·∫≠p m·∫∑n Tp. H·ªì Ch√≠ Minh</title>
            </Helmet>
            <Header />
            <div className="infoUser">
                <div className="settings-container">
                    <div className="settings-header">
                        <h1>C√†i ƒë·∫∑t t√†i kho·∫£n</h1>
                        <p>C·∫≠p nh·∫≠t th√¥ng tin c√° nh√¢n c·ªßa b·∫°n</p>
                    </div>

                    <div className="profile-section">
                        <form
                            className="settings-form"
                            onSubmit={(e) => {
                                e.preventDefault();
                                handleSubmit();
                            }}
                        >
                            <div className="form-sections">
                                <div className="inputGroup">
                                    <label htmlFor="name">H·ªç v√† t√™n</label>
                                    <input
                                        type="text"
                                        id="name"
                                        name="name"
                                        value={formState.name}
                                        onChange={handleChange}
                                        placeholder="Nh·∫≠p h·ªç v√† t√™n c·ªßa b·∫°n"
                                    />
                                    <FaUser className="input-icon" />
                                </div>

                                <div className="inputGroup">
                                    <label htmlFor="email">Email</label>
                                    <input
                                        type="email"
                                        id="email"
                                        name="email"
                                        value={formState.email}
                                        onChange={handleChange}
                                        disabled
                                        placeholder="Nh·∫≠p email c·ªßa b·∫°n"
                                    />
                                    <FaEnvelope className="input-icon" />
                                </div>

                                <div className="inputGroup">
                                    <label htmlFor="birthday">Ng√†y sinh</label>
                                    <input
                                        type="date"
                                        id="birthday"
                                        name="birthday"
                                        value={formState.birthday}
                                        onChange={handleChange}
                                    />
                                    <FaCalendar className="input-icon" />
                                </div>

                                <div className="inputGroup">
                                    <label htmlFor="phone">S·ªë ƒëi·ªán tho·∫°i</label>
                                    <input
                                        type="tel"
                                        id="phone"
                                        name="phone"
                                        value={formState.phone}
                                        onChange={handleChange}
                                        placeholder="Nh·∫≠p s·ªë ƒëi·ªán tho·∫°i c·ªßa b·∫°n"
                                    />
                                    <FaPhone className="input-icon" />
                                    {errorMessages.phone && (
                                        <span className="error">{errorMessages.phone}</span>
                                    )}
                                </div>
                            </div>

                            <div className="password-toggle">
                                <input
                                    type="checkbox"
                                    id="togglePassword"
                                    checked={toggleUpdatePassword}
                                    onChange={(e) => setToggleUpdatePassword(e.target.checked)}
                                />
                                <label htmlFor="togglePassword">ƒê·ªïi m·∫≠t kh·∫©u</label>
                            </div>

                            {toggleUpdatePassword && (
                                <div className="form-sections">
                                    <div className="inputGroup">
                                        <label htmlFor="password">M·∫≠t kh·∫©u m·ªõi</label>
                                        <input
                                            type="password"
                                            id="password"
                                            name="password"
                                            value={formState.password}
                                            onChange={handleChange}
                                            placeholder="Nh·∫≠p m·∫≠t kh·∫©u m·ªõi"
                                        />
                                        <FaKey className="input-icon" />
                                        {errorMessages.password && (
                                            <span className="error">{errorMessages.password}</span>
                                        )}
                                    </div>

                                    <div className="inputGroup">
                                        <label htmlFor="confirmPassword">X√°c nh·∫≠n m·∫≠t kh·∫©u</label>
                                        <input
                                            type="password"
                                            id="confirmPassword"
                                            name="confirmPassword"
                                            value={formState.confirmPassword}
                                            onChange={handleChange}
                                            placeholder="X√°c nh·∫≠n m·∫≠t kh·∫©u m·ªõi"
                                        />
                                        <FaLock className="input-icon" />
                                        {errorMessages.confirmPassword && (
                                            <span className="error">{errorMessages.confirmPassword}</span>
                                        )}
                                    </div>
                                </div>
                            )}

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="cancel-button"
                                    onClick={() =>
                                        setFormState({
                                            ...userInfo,
                                            password: "",
                                            confirmPassword: "",
                                        })
                                    }
                                >
                                    <FaTimes />
                                    H·ªßy
                                </button>
                                <button type="submit" className="save-button">
                                    <FaSave />
                                    L∆∞u thay ƒë·ªïi
                                </button>
                            </div>
                        </form>
                    </div>
                </div>

                {showConfirmModal && (
                    <ModalConfirm
                        message={UPDATE.user}
                        onConfirm={confirmAction}
                        onCancel={() => setShowConfirmModal(false)}
                    />
                )}
            </div>
        </>
    );
}

