import axios from "axios";
import { TOAST } from "@common/constants";
import { validateFormLogin, validateFormSignUp } from "@common/validate";
import { ToastCommon } from "@components/ToastCommon.jsx";
import { SET_SHOW_SIGNUP, SET_USER_INFO } from "@stores/constants";
import { persistor } from "@stores/store";
import { jwtDecode } from "jwt-decode";

export const signUp = (params) => {
    return async (dispatch, getState) => {
        try {
            // validation
            validateFormSignUp(params);

            const resp = await axios.post(import.meta.env.VITE_BASE_URL + "/api/signup", {
                name: params.name,
                email: params.email,
                password: params.password,
            });

            if (resp) {
                ToastCommon(
                    TOAST.SUCCESS,
                    "Successfully registered! Please check your email to verify your account.",
                );
                dispatch({
                    type: SET_SHOW_SIGNUP,
                    payload: false,
                });
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};

export const login = (params, onRequestNavigate) => {
    return async (dispatch, getState) => {
        try {
            validateFormLogin(params);

            const resp = await axios.post(import.meta.env.VITE_BASE_URL + "/api/login", params);

            if (resp) {
                // L∆∞u token v√†o localStorage
                localStorage.setItem("access_token", resp.data.access_token);
                localStorage.setItem("refresh_token", resp.data.refresh_token);

                dispatch({
                    type: SET_USER_INFO,
                    payload: jwtDecode(resp.data.access_token),
                });

                onRequestNavigate();
            }
        } catch (error) {
            // Ki·ªÉm tra n·∫øu l√† l·ªói x√°c th·ª±c email ch∆∞a
            if (error?.response?.data?.message) {
                if (error.response.data.message === "Please verify your email before logging in.") {
                    ToastCommon(
                        TOAST.ERROR,
                        "B·∫°n ch∆∞a x√°c th·ª±c email. Vui l√≤ng ki·ªÉm tra h·ªôp th∆∞ c·ªßa b·∫°n ƒë·ªÉ x√°c th·ª±c email.",
                    );
                } else {
                    ToastCommon(TOAST.ERROR, error.response.data.message);
                }
            } else {
                ToastCommon(TOAST.ERROR, error.message);
            }
        }
    };
};

export const logout = () => {
    return async (dispatch, getState) => {
        localStorage.removeItem("access_token");
        localStorage.removeItem("refresh_token");
        persistor.purge();
    };
};

import { TOAST } from "@common/constants";
import { ToastCommon } from "@components/ToastCommon";
import axiosInstance from "@config/axios-config";
import { SET_LIST_USER, SET_USER_INFO } from "@stores/constants";
import { hideLoading, showLoading } from "@stores/actions/appAction";
import { logout } from "@stores/actions/authActions";

export const getListUser = () => {
    return async (dispatch, getState) => {
        try {
            dispatch(showLoading());
            const resp = await axiosInstance.get(import.meta.env.VITE_BASE_URL + "/api/user");

            if (resp) {
                dispatch({
                    type: SET_LIST_USER,
                    payload: resp.data,
                });

                const userByEmail = resp.data.find(
                    (user) => user.email === getState().authStore.userInfo.email,
                );

                dispatch({
                    type: SET_USER_INFO,
                    payload: userByEmail,
                });

                dispatch(hideLoading());
            }
        } catch (error) {
            console.log(error.response?.data?.message);
            dispatch(hideLoading());
        }
    };
};

export const createUser = (params) => {
    return async (dispatch, getState) => {
        try {
            const resp = await axiosInstance.post(import.meta.env.VITE_BASE_URL + "/api/user", params);
            if (resp) {
                document.getElementById("close-create-user-btn").click();
                ToastCommon(TOAST.SUCCESS, "Created user successfully");
                dispatch(getListUser());
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};

export const deleteUser = (params) => {
    return async (dispatch, getState) => {
        try {
            const resp = await axiosInstance.delete(import.meta.env.VITE_BASE_URL + "/api/user", {
                data: params,
            });
            if (resp) {
                dispatch(getListUser());
                ToastCommon(TOAST.SUCCESS, "Deleted user successfully");
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};

export const updateUser = (params) => {
    return async (dispatch, getState) => {
        try {
            const res = await axiosInstance.put(import.meta.env.VITE_BASE_URL + "/api/user", params);

            if (res) {
                document.getElementById("close-edit-user-btn").click();
                ToastCommon(TOAST.SUCCESS, "Updated user successfully");
                dispatch(getListUser());
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};

export const updateUserByUser = (params) => {
    return async (dispatch, getState) => {
        try {
            let request = {
                email: params.email,
                name: params.name,
                phone: params.phone,
                birthday: params.birthday,
            };

            if (params.password && params.password.length > 0) {
                request = {
                    ...request,
                    password: params.password,
                };
            }

            let success = false;

            const resUser = await axiosInstance.put(import.meta.env.VITE_BASE_URL + "/api/user", request);
            if (resUser.status === 200) {
                success = true;
            }

            if (success) {
                ToastCommon(TOAST.SUCCESS, "Updated user successfully");
                dispatch(getListUser());
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};

export const changeRole = (params, navigate) => {
    return async (dispatch, getState) => {
        try {
            const resp = await axiosInstance.put(import.meta.env.VITE_BASE_URL + "/api/user/changerole", {
                email: params.email,
                role: params.role,
            });

            if (resp) {
                ToastCommon(TOAST.SUCCESS, "Role has been changed successfully.");
                dispatch(getListUser());
                if (getState().authStore.userInfo.email === params.email) {
                    dispatch(logout());
                    navigate();
                }
            }
        } catch (error) {
            ToastCommon(TOAST.ERROR, error.response?.data?.message || error.message);
        }
    };
};
